import React from "react";
import "../css/Main.css"
import RankTable from '../components/RankTable';
class MainPage extends React.Component {

    constructor(props) {
      super(props);
      this.score = {}
      this.state = {
        isLoginOpen: true,
        reactTime_rank: undefined,
        reactTime_myScore: undefined
      };
    }

    componentDidMount(){
      console.log("Main Mounted")
      this.props.socket.emit("test",["Main Mounted",this.props.acc])
      this.props.socket.on("reactTime_Best",(props) => {
        this.setState({reactTime_rank : props[0]})
        this.setState({reactTime_myScore: props[1]})
        console.log(props)
        console.log("Ranking Recieved")
      })

    }

    logout = () => {
        console.log("Logout")
        this.props.socket.emit('logout')
    }

    login = () => {
        console.log("loginPage")
        this.props.socket.emit('loginPage')
    }

    toReactTime = () => {
        console.log("ReactTime")
        this.props.socket.emit('reactTime',this.props.acc)
    }
    render() {
      return (
        <body>
			<section id="banner">
                {this.props.logged && <span className="userBar">
                    <div className="userName"> {this.props.acc.username}</div>
                    <div onClick={this.logout} class="logout">logout</div>
				</span>}
                <h2><strong>MYHUMANBENCHMARK</strong> an imitation of www.humanbenchmark.com</h2>
				<p>A place to measure your abilites and track your stats</p>
				{this.props.logged && <ul class="actions">
					<li><div onClick={this.toReactTime}className="challenge"><i class="fa fa-eye fa-3x"></i></div></li>
                    <li><div className="challenge"><i class="fa fa-sort-numeric-asc fa-3x"></i></div></li>
                    <li><div className="challenge"><i class="fa fa-share-square-o fa-3x"></i></div></li>
                </ul>}
                {!this.props.logged && <ul class="actions">
					<li><div onClick={this.login} className="login"><span>login</span></div></li>
                </ul>}
			</section>
            {this.props.logged && <section id="one" class="wrapper special">
				<div class="inner">
					<header class="major">
						<h2>Challenge Introduction</h2>
					</header>
					<div class="features">
						<div class="feature">
							<i class="fa fa-eye "></i>
							<h3>Reaction Time</h3>
							<p>Check your visual reflexed</p>
              <p>Your Best: {this.state.reactTime_myScore} ms</p>
              <p>Top Ten</p>
              {this.state.reactTime_rank && <RankTable rankList = {this.state.reactTime_rank}/>}
						</div>
						<div class="feature">
							<i class="fa fa-sort-numeric-asc"></i>
							<h3>Number Memory</h3>
							<p>Remember the longest number you can</p>
						</div>
            <div class="feature">
							<i class="fa fa-share-square-o"></i>
							<h3>Visual Memory</h3>
							<p>Remember an increasingly large board of squares</p>
						</div>
					</div>
				</div>
			</section>}

        </body>
      );
    }
  }

  export default MainPage
