import React from "react";
import socketIOClient from "socket.io-client";
import "./Login.css"
class Login extends React.Component {

    constructor(props) {
      super(props);
      this.state = {
        isLoginOpen: true
      };
    }

    showLoginBox() {
      this.setState({isLoginOpen: true});
    }

    showRegisterBox() {
      this.setState({isLoginOpen: false});
    }

    componentDidMount(){
      console.log("Login Mounted")
      this.props.socket.emit('test',"test")
    }

    render() {
      return (
        <div className="root-container">

          <div className="box-controller">
            <div
              className={"controller " + (this.state.isLoginOpen
              ? "selected-controller"
              : "")}
              onClick={this
              .showLoginBox
              .bind(this)}>
              Login
            </div>
            <div
              className={"controller " + (this.state.isRegisterOpen
              ? "selected-controller"
              : "")}
              onClick={this
              .showRegisterBox
              .bind(this)}>
              Register
            </div>
          </div>

          {this.state.isLoginOpen && <div className="box-container"><LoginBox socket={this.props.socket}/></div>}
          {!this.state.isLoginOpen && <div className="box-container"><RegisterBox socket={this.props.socket}/></div>}

        </div>
      );

    }

  }

  class LoginBox extends React.Component {

    constructor(props) {
      super(props);
      this.state = {
        username : "",
        password : ""
      };
    }

    submitLogin(e) {}

    render() {
      return (
        <div className="inner-container">
          <div className="header">
            Login
          </div>
          <div className="box">

            <div className="input-group">
              <label htmlFor="username">Username</label>
              <input
                type="text"
                name="username"
                className="login-input"
                placeholder="Username"/>
            </div>

            <div className="input-group">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                name="password"
                className="login-input"
                placeholder="Password"/>
            </div>

            <button
              type="button"
              className="login-btn"
              onClick={this
              .submitLogin
              .bind(this)}>Login</button>

          </div>
        </div>
      );
    }

  }

  class RegisterBox extends React.Component {

    constructor(props) {
      super(props);
      this.state = {
        username : "",
        password : ""
      };
    }

    submitLogin(e) {}

    render() {
      return (
        <div className="inner-container">
          <div className="header">
            Register
          </div>
          <div className="box">

            <div className="input-group">
              <label htmlFor="username">Username</label>
              <input
                type="text"
                name="username"
                className="login-input"
                placeholder="Username"/>
            </div>

            <div className="input-group">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                name="password"
                className="login-input"
                placeholder="Password"/>
            </div>

            <button
              type="button"
              className="login-btn"
              onClick={this
              .submitLogin
              .bind(this)}>Register</button>

          </div>
        </div>
      );
    }

  }

export default Login
